        <input id="box" type="number" style="width:50px;" max="100" min="1" formControlName="boxes" />
      </label>
      <label for="name">
        Title
        <input id="name" type="text" formControlName="name" />
      </label>
      <label for="superzahl">
        Generate superzahl
        <input id="superzahl" type="checkbox" formControlName="superzahl" />
      </label>
    </div>
    <button type="button" [disabled]="form.controls['boxes'].value == null" id="generate" class="clearfix" (click)="generateTicket(form.controls['boxes'].value)">Draw ticket</button>
  </form>
</div>
